pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

import "./utility/Lockable.sol";
import "./utility/SafeMath.sol";
import "./utility/Address.sol";
import "./utility/AddressBook.sol";
import "./interfaces/IAggregatorV3.sol";
import "./interfaces/IERC20.sol";
import "./interfaces/IBurner.sol";
import "./TokenFactory.sol";

/**
 * @title Burner contract
 */



contract Burner is Lockable, AddressBook, IBurner {
    using SafeMath for uint256;
    using Address for address;

    /**
     * @dev version number
     */
    uint16 public constant version = 1;

    // Max duration since the latest timestamp identified by Oracle 
    uint constant public guardInterval = 86400;

    string private name;
    string private symbol;
    // Allowed ERC-20 tokens to be burnt
    mapping(address => bool) public acceptedTokens;
    // Synthetic token created by this contract.
    IExpandedIERC20 public tokenCurrency;
    // Chainlink's Fiat Currency price feed (JPY/USD, GBP/USD, EUR/USD)
    IAggregatorV3 internal priceFeed;

    constructor(
        string memory _syntheticName,
        string memory _syntheticSymbol,
        address _chainlinkPriceFeedAddress,
        address _tokenFactoryAddress
    ) public nonReentrant() {

        name = _syntheticName;
        symbol = _syntheticSymbol;

        priceFeed = IAggregatorV3(_chainlinkPriceFeedAddress);
        TokenFactory tf = TokenFactory(_tokenFactoryAddress);
        tokenCurrency = tf.createToken(_syntheticName, _syntheticSymbol, 18);

        // Setup stablecoins to be burnt
        acceptedTokens[_getAddressUSDC()] = true;
        acceptedTokens[_getAddressPAX()] = true;
        acceptedTokens[_getAddressTUSD()] = true;
        acceptedTokens[_getAddressDAI()] = true;
    }

    function getName() public view override returns (string memory) {
        return name;
    }

    function getSymbol() public view override returns (string memory) {
        return symbol;
    }

    /**
     * @dev Check if the given token address is allowed to be burnt
     * @param _address Token address
     */
    function isAccepted(address _address) external view returns (bool) {
        return acceptedTokens[_address];
    }

    /**
     * @dev Mint corresponding synthetic tokens
     */
    function mint(
        address tokenAddress,
        uint256 amountOut,
        uint256 maxAmountIn
        )
        public
        nonReentrant()
        onlyLatest()
    {
        require(acceptedTokens[tokenAddress] == true, "Invalid base token address");
        IToken baseToken = IToken(tokenAddress);

        uint256 decimals = baseToken.decimals();

        uint256 amountIn = calculateAmountIn(amountOut);
        if (decimals != 18) {
            amountIn = amountIn.div(10**(18-decimals));
        }

        require(maxAmountIn >= amountIn, "Exceeding max amount");

        // Transfer tokens to this contract from caller and mint corresponding synthetic tokens to the caller's address.
        // FIXME: Use safeTransferFrom()
        baseToken.transferFrom(msg.sender, address(this), amountIn);
        require(tokenCurrency.mint(msg.sender, amountOut), "Minting synthetic tokens failed");

    }

    /**
     * @dev Calculate output amount
     */
    function calculateAmountOut(
        uint256 amountIn
    ) public view onlyLatest() returns (uint256) {
        return amountIn.div(price()).mul(1e8);
    }

    /**
     * @dev Calculate input amount
     */
    function calculateAmountIn(
        uint256 amountOut
    ) public view onlyLatest() returns (uint256) {
        return amountOut.mul(price()).div(1e8);
    }

    /**
     * @dev Returns the latest price
     */
    function price() public view returns (uint256) {
        (
            // uint80 roundID, 
            ,int latestPrice,,,
            // uint startedAt,
            // uint timeStamp,
            // uint80 answeredInRound
        ) = priceFeed.latestRoundData();
        return uint256(latestPrice);
    }

    /**
     * @dev Returns the latest timestamp
     */
    function timestamp() public view returns (uint) {
        (
         ,,,uint latestTimeStamp,
        ) = priceFeed.latestRoundData();
        return latestTimeStamp;
    }


    modifier onlyLatest() {
        (
         ,,,uint latestTimeStamp,
        ) = priceFeed.latestRoundData();
        uint currentTimestamp = block.timestamp;
        require(latestTimeStamp > (currentTimestamp - guardInterval) , "Price Feed is not active.");
        _;
    }

}
